// src/gatsby-theme-dox/theme.js
import theme from 'gatsby-theme-dox/src/theme'

export default {
  ...theme,
  colors: {
    ...theme.colors,
    primary: '#FFCD00'
  }
}

// src/gatsby-theme-dox/components/Logo.js
import React from 'react'

function Logo(props) {
  return <img src="/logo.png" alt="Go to homepage" {...props} />
}

export default Logo

